import React, { Component, useState } from 'react';
import Form from './components/Form';
import YolNowTemplate from './components/YolNowTemplate';
import Palette from './components/Palette';
import ContentItemList from './components/ContentItemList';
import moment from 'moment';
import 'moment/locale/ko';
import { useInterval } from 'react-use';

const colors = ['#343a40', '#f03e3e', '#12b886', '#228ae6'];
const LiveTimeContainer = () => {
  const [realTime, setRealTime] = useState(Date,now());

  useInterval(() => {
    setRealTime(Date.now());
  }, 1000);
}

class App extends Component {

  id = 1;

  state = {
    input: '',
    contents: [
      { id: 'EYol', time: '2021-02-17 11:40:22' ,text: 'yol' } // [시간]아이디 : 내용
    ],
    color: '#343a40'
  }

  handleChange = (e) => {
    this.setState({
      input: e.target.value
    });
  }

  handleCreate = () => {
    const { input, contents, seconds, color } = this.state;
    this.setState({
      input: '',
      contents: contents.concat({
        id: this.id,
        time: seconds,
        text: input,
        color
      })
    });
  }

  handleKeyPress = (e) => {
    if(e.key === 'Enter') {
      this.handleCreate();
    }
  }

  handleSelectColor = (color) => {
    this.setState({
      color
    })
  }

  render() {

    const { input, contents, seconds, color } = this.state;
    const {
      handleChange,
      handleCreate,
      handleKeyPress,
      handleSelectColor
    } = this;

    return (
      <YolNowTemplate form={(
        <Form 
        value={input}
        onKeyPress={handleKeyPress}
        onChange={handleChange}
        onCreate={handleCreate}
        color={color}
        time={seconds}
      />
      )}
        palette={(
          <Palette colors={colors} selected={color} onSelect={handleSelectColor}/>
        )}
      >
        <ContentItemList contents={contents}/>     
      </YolNowTemplate>            
    );
  }
}

export default App;
