import React, { Component } from 'react';
import Form from './components/Form';
import YolNowTemplate from './components/YolNowTemplate';

const colors = ['#343a40', '#f03e3e', '#12b886', '#228ae6'];

class App extends Component {

  state = {
    input: '',
    yol: [
      { id: 0, time: '2021-02-17' ,text: 'yol' } // [시간]아이디 : 내용
    ],
    color: '#343a40'
  }

  handleChange = (e) => {
    this.setState({
      input: e.target.value
    });
  }

  handleCreate = () => {
    const { input, color } = this.state;
    this.setState({
      input: '',
    })
  }

  render() {

    const { input, color } = this.state;

    return (
      <YolNowTemplate form={(
        <Form 
        value={input}
        color = {color}
      />
      )}>        
      </YolNowTemplate>            
    );
  }
}

export default App;
