import React, { Component } from 'react';
import Form from './components/Form';
import YolNowTemplate from './components/YolNowTemplate';
import Palette from './components/Palette';

const colors = ['#343a40', '#f03e3e', '#12b886', '#228ae6'];

class App extends Component {

  state = {
    input: '',
    content: [
      { id: 0, time: '2021-02-17' ,text: 'yol' } // [시간]아이디 : 내용
    ],
    color: '#343a40'
  }

  handleChange = (e) => {
    this.setState({
      input: e.target.value
    });
  }

  handleCreate = () => {
    const { input, content, color } = this.state;
    this.setState({
      input: '',
      content: content.concat({
        id,
        time,
        text: input,
        color
      })
    });
  }

  handleKeyPress = (e) => {
    if(e.key === 'Enter') {
      this.handleCreate();
    }
  }

  handleSelectColor = (color) => {
    this.setState({
      color
    })
  }

  render() {

    const { input, time, color } = this.state;
    const {
      handleChange,
      handleCreate,
      handleKeyPress,
      handleSelectColor
    } = this;

    return (
      <YolNowTemplate form={(
        <Form 
        value={input}
        color={color}
      />
      )}
        palette={(
          <Palette colors={colors} selected={color} onSelect={handleSelectColor}/>
        )}
      >        
      </YolNowTemplate>            
    );
  }
}

export default App;
