import React, { Component } from 'react';
import Form from './components/Form';
import YolNowTemplate from './components/YolNowTemplate';
import Palette from './components/Palette';
import ContentItemList from './components/ContentItemList';

const colors = ['#343a40', '#f03e3e', '#12b886', '#228ae6']; // 선택가능한 색

class App extends Component {

  id = 0; // 배열 키 값

  state = {
    input: '',
    nickname: 'Yol',
    contents: [
      // (시간)아이디 : 내용
    ],
    color: '#343a40',
    time: new Date()
  }   

  handleChange = (e) => {
    this.setState({
      input: e.target.value
    });
  }

  handleCreate = () => {
    const { input, contents, color } = this.state;
    const time = new Date()
    const nowTime = time.getFullYear() + '-' + (time.getMonth() + 1).toString().padStart(2, '0') + '-' 
                    + time.getDay().toString().padStart(2, '0') + ' ' + time.getHours() + ':' 
                    + time.getMinutes().toString().padStart(2, '0') + ':' + time.getSeconds().toString().padStart(2, '0');
    this.setState({
      input: '',
      contents: contents.concat({
        id: this.id++,
        nickname: this.nickname,        
        text: input,
        color,
        time: nowTime
      })      
    });
  }

  handleKeyPress = (e) => {
    if(e.key === 'Enter') {
      this.handleCreate();
    }
  }

  handleSelectColor = (color) => {
    this.setState({
      color
    })
  }

  render() {

    const { input, contents, color, time } = this.state;
    const {
      handleChange,
      handleCreate,
      handleKeyPress,
      handleSelectColor,
    } = this;    

    return (      
      <YolNowTemplate form={(
        <Form 
        value={input}
        onKeyPress={handleKeyPress}
        onChange={handleChange}
        onCreate={handleCreate}
        color={color}   
        time={time}     
      />
      )}
        palette={(
          <Palette colors={colors} selected={color} onSelect={handleSelectColor}/>
        )}
      >
        <ContentItemList contents={contents}/>     
      </YolNowTemplate>            
    );
  }
}

export default App;
