import React, { Component } from 'react';
import Form from './components/Form';

const colors = ['#343a40', '#f03e3e', '#12b886', '#228ae6'];

class App extends Component {

  state = {
    input: '',
    color: '#343a40'
  }

  handleChange = (e) => {
    this.setState({
      input: e.target.value
    });
  }

  render() {

    const { input, color } = this.state;

    return (
      <Form 
        value={input}
        color = {color}
      />      
    );
  }
}

export default App;
